import React, {useEffect, useState} from 'react';
import {useParams} from "react-router-dom";
import { getFruit } from 'API/API';
import { FruitPageStyled } from './FruitPage.styled';
import { Loader } from 'components/Loader/Loader';
import { useSelector, useDispatch } from 'react-redux'
import { addFruit } from 'containers/App/storage/fruitSlice';
import {NiceButtonGreen } from 'components/NiceButton';


export const FruitPage = () => {
    const count = useSelector((state) => state.fruitInCart.value)
    const dispetch = useDispatch()
    let error=0;
    const {fruitId} = useParams();
    const [fruit, setFruit] = useState();
    const [isLoaded, setIsLoaded] = useState(false);
    useEffect(() => {
        getFruit(fruitId).then(recievedFruit => {
            setFruit(recievedFruit)
            setIsLoaded(true)
            })
    }, [fruitId, count.length])

    let image =""
    if(fruit!==undefined){
        switch(fruit.fruitKind){
            case("BERRY"): image="https://media.istockphoto.com/photos/wild-berry-mix-strawberries-blueberries-blackberries-and-raspberries-picture-id499658564?k=20&m=499658564&s=612x612&w=0&h=jWWLTXolflgwzPSQ-Hy7OkXFXZqSjJaSSL5BG0c34Ck="
            break
            case("CITRUS"): image ="data:image/jpeg;base64,"
            break
            case("FRUIT"): image = "https://media.istockphoto.com/photos/fruit-background-picture-id529664572?k=20&m=529664572&s=612x612&w=0&h=UPKnmT4wmX8KB_nke3R0Y6mH_syPYn7KTKFFYLJEJ8U="
            break
            case("TROPICAL"): image="data:image/jpeg;base64,"
            break
            case("MELON"): image="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSkRGOL6ulNaShbEVfSy3okuqM0b_aSFuXK-g&usqp=CAU"
            break
            default:
                break
        }
    }
    if (fruit ===undefined){
        isLoaded ? 
        error=404 : error=-1
    }
    return (
        (error===-1)?
        <Loader />:
        (error===404)?
        <h1> Not Found </h1>:
        <FruitPageStyled>
            <img src= {image} alt={fruit.name}/>
            <div className="description">
                <h1>{fruit.name}</h1>
                <p>The description of this fruit is yet to be written but it would be diplayed here.</p>
                <ul>
                    <li>Color: {fruit.color}</li>
                    <li>In cart: {count.length}</li>
                    <li>Season: {fruit.season}</li>
                    <li>Fruit kind: {fruit.fruitKind}</li>
                    <li>Price: <br/>
                        { fruit.price ? "$" + fruit.price : " unknown"}
                    </li>
                </ul>
                <NiceButtonGreen style={{marginLeft:"300px"}} onClick={() => dispetch(addFruit(fruit))}>Add to cart</NiceButtonGreen>
            </div>
        </FruitPageStyled>
    );
}