export const addFruitToPage = (fruit) => {
  let image = fruit_img.get(fruit.fruitKind);
  cardsContainer.insertAdjacentHTML(
    "beforeend",
    cardText({ Fruit: fruit, image: image })
  );
};
export const renderEditButtons = () => {
  for (let fruit of renderedFruits) {
    let edit_icon = document.getElementById(`edit-icon-${fruit.id}`);
    edit_icon.addEventListener("click", () => {
      update_btn.style.display = "block";
      send_btn.style.display = "none";
      modal.style.display = "block";
      modal_header_text.innerHTML = editFruitHeader;
      modal_body.innerHTML = editFruitForm({ Fruit: fruit });
    });
  }
};
export const renderDeleteButtons = () => {
  for (let fruit of renderedFruits) {
    let delete_icon = document.getElementById(`delete-icon-${fruit.id}`);
    delete_icon.addEventListener("click", () => {
      const deletedFruit = deleteFruit(fruit.id)
      var audio = new Audio("./sound/hit_sound.mp3")
      audio.play()
      let index = fruits.indexOf(fruit);
      fruits.splice(index, 1);
      selectFruits();
      renderFruits();
    });
  }
};

export const renderFruits = () => {
  cards.innerHTML = "";
  renderedFruits.forEach((fruit) => addFruitToPage(fruit));
  page_number.innerHTML = page;
  slime_sound();
  renderEditButtons();
  renderDeleteButtons();
};

add_fruit.addEventListener("click", (event) => {
  send_btn.style.display = "block";
  update_btn.style.display = "none";
  modal.style.display = "block";
  modal_header_text.innerHTML = " ";
  modal_header_text.insertAdjacentHTML("afterbegin", addNewFruitHeader);
  modal_body.innerHTML = "";
  modal_body.insertAdjacentHTML("afterbegin", addNewFruitBody);
});

send_btn.addEventListener("click", () => {
  fruit_name = document.getElementById("name_input");
  fruit_price = document.getElementById("price_input");
  fruit_color = document.getElementById("color_input");
  fruit_kind = document.getElementById("KIND_input");
  fruit_season = document.getElementById("season-input");
  let fname = fruit_name.value;
  let fprice = fruit_price.value;
  let fcolor = fruit_color.value.toUpperCase();
  let fkind = fruit_kind.value.toUpperCase();
  let fseason = fruit_season.value.toUpperCase();
  let newFruit = new Fruit(null, fseason, fcolor, fprice, fkind, fname);
  postFruit(newFruit);
  let audio = new Audio('./sound/artas.mp3')
  audio.play()
  fruits.push(newFruit);
  modal.style.display = "none";
  selectFruits();
  renderFruits(fruits, page);
});

update_btn.addEventListener("click", () => {
  fruitIdelem = document.getElementById("fruit-Id");
  fruit_name = document.getElementById("name_input");
  fruit_price = document.getElementById("price_input");
  fruit_color = document.getElementById("color_input");
  fruit_kind = document.getElementById("KIND_input");
  fruit_season = document.getElementById("season-input");
  let fruitId = fruitIdelem.textContent;
  let fname = fruit_name.value;
  let fprice = fruit_price.value;
  let fcolor = fruit_color.value.toUpperCase();
  let fkind = fruit_kind.value.toUpperCase();
  let fseason = fruit_season.value.toUpperCase();
  let newFruit = new Fruit(fruitId, fseason, fcolor, fprice, fkind, fname);
  updateFruit(fruitId, newFruit);
  let audio = new Audio('./sound/power.mp3')
  audio.play()
  modal.style.display = "none";
  let index;
  fruits.forEach((element) => {
    if (element.id == fruitId) {
      index = fruits.indexOf(element);
    }
  });
  fruits[index] = newFruit;
  selectFruits();
  renderFruits(fruits, page);
});

function slime_sound() {
  var audio = new Audio("./sound/slimejump.mp3");
  audio.play();
}
select_color.addEventListener("click", (event) => {
  if (document.getElementsByClassName("light")[0] == undefined) {
    document.body.classList.replace("dark", "light");
    fruit_img = light_fruit_img;
    select_color.textContent = "Choose dark side"
    select_color.style.color = "black"
  } else {
    document.body.classList.replace("light", "dark");
    fruit_img = dark_fruit_img;
    select_color.textContent = "Return to light"
    select_color.style.color = "whitesmoke"
  }
  renderFruits();
});
